package application;

public class 증감연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count = 0;
		count = count + 1;
		System.out.println(count);
		count += 1;
		System.out.println(count);
		count ++;
		System.out.println(count);
		++count;
		System.out.println(count);
		
		count = count - 1;
		System.out.println(count);
		count -= 1;
		System.out.println(count);
		count --;
		System.out.println(count);
		--count;
		System.out.println(count);
		
		
		
	}

}
