package examples;

public class Example7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 24;
		int b = 6;
		int c = a*b;
		int d = a/b;
		int e = a%b;
		//System.out.println(rest);
		System.out.printf("곱하기: %d x %d = "+c, a, b);
		System.out.printf("\n나누기 몫: %d / %d = "+d, a, b);
		System.out.printf("\n나누기 나머지: %d / %d = "+e, a, b);
		
	}

}
